
public class switchcase {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
     int n=3;
     
     if(n==1)
     {
    	 System.out.println("one");
     }
     else if(n==2)
     {
    	 System.out.println("two");
     }
     else if(n==3)
     {
    	 System.out.println("three");
     }
     else
     {
    	 System.out.println("not a valid number");
     }
	}

}
